$("#searchText").autocomplete({source:function(e,t){$.ajax({type:"GET",url:"https://en.wikipedia.org/w/api.php?action=opensearch&search="+e.term+"&format=json&callback?",dataType:"jsonp",success:function(e){t(e[1])}})},select:function(e,t){$("#searchText").val(t.item.value),$("#form-container").submit()}}),$("#searchText").keypress(function(e){if(e||(e=window.event),"13"==e.keyCode)return $("#searchText").autocomplete("close"),$("#form-container").submit(),!1}),$("#form-container").submit(function(e){e.preventDefault();var t=$("#searchText").val(),s="https://en.wikipedia.org/w/api.php?action=query&list=search&srsearch="+t+"&format=json";$.ajax({type:"GET",url:s,dataType:"jsonp",headers:{"Api-User-Agent":"Example/1.0"},success:function(e){if($("#results").html(""),e.hasOwnProperty("error"))$("#results").append('<div class="text-center col-md-6 col-md-offset-3 col-sm-6 col-sm-offset-3 col-xs-8 col-xs-offset-2 result"><h4>Error encountered. Please try again later!</h4></div>');else if(0===e.query.search.length)$("#results").append('<div class="text-center col-md-6 col-md-offset-3 col-sm-6 col-sm-offset-3 col-xs-8 col-xs-offset-2 result"><h4>No Results Found</h4></div>');else for(var t=e.query.search,s=0;s<t.length;s++){var r=t[s].title.replace(/ /g,"_"),o=t[s].title,a=t[s].snippet;$("#results").append("<a href=http://en.wikipedia.org/wiki/"+r+' target="blank_"><div class="col-md-6 col-md-offset-3 col-sm-6 col-sm-offset-3 col-xs-8 col-xs-offset-2 result"><h4>'+o+'</h4><p class="description"><p>'+a+"<p></div></a>")}},error:function(){$("results").html("Error encountered. Please try again later!")}})});